syntax = "proto3";

package cmd;

//生成go文件的路径
option go_package          = "cmd/";

message BaseCmd {
    int32 CmdId = 1;  // 消息号
    bytes Data = 2; // 消息体
    int32 ReqId = 3; // 请求号 做请求响应使用,服务器需要响应消息里放同样的reqId
    int32 code = 4; // 错误id 0 正常响应其他是错误
    string Msg = 5;// 错误信息
}


message CharacterInfo {
    int32 id = 1; //character表id
    bool inBattle = 2;	//是否出战
    int64 refreshTime = 3; //出战cd到期时间，单位毫秒
}



// 用户基础信息
message UserInfo {
    string Uid = 1;  // 用户uid
    string NickName = 2; // 用户昵称
    string Avatar = 3; // 头像
    int32 Gender = 4; // 性别
    int64 Level = 5;// 等级
    int64 Coin = 6;// 银币
    int64 TotalExp = 7;// 经验
    int64 Diamond = 8;// 钻石
    int64 Ticket = 9;// 奖券
    string LevelBadge = 10;// 等级徽章
    string AvatarFrame = 11;// 头像框
    double Balance = 12;//余额
    bool inGame = 13; //是否正在游戏
    repeated CharacterInfo characterList = 14; //角色列表(只有正在游戏时才有意义)
}

// 心跳
message ReqPing {

}

message RetPing {
    int64 time = 1; // 时间
}

//获得玩家信息
message ReqGetUserInfo {

}

message RetGetUserInfo {
   UserInfo info = 1; //信息 
}

//开始游戏
message ReqStartGame {
   int32 difficultyId = 1; //difficulty表id
}

message RetStartGame {
   bool inGame = 1; //是否正在游戏
   repeated CharacterInfo characterList = 2; //角色列表
   double  balance = 3; //余额
}

//出战
message ReqStartBattle {
   int32 id = 1; //character表id
}

message RetStartBattle {
   CharacterInfo info = 1; //角色信息
   double  balance = 2; //余额
}


//角色死亡通知
message ReqUpdateCharacterDie {
   int32 id = 1; //character表id
}

message RetUpdateCharacterDie {
   CharacterInfo info = 1; //角色信息
}

//游戏结果
message ReqGameResult {
   bool win = 1; //是否胜利
}

message RetGameResult {
   double  winAmount = 1; //赢得多少数量
   double  balance = 2; //余额
}


//-----------广播消息----------------------

//被顶号
message EventOnReconnect {

}

//服务器准备
message EventServerReady {
  UserInfo info = 1; //信息 
}
